import { useContext, useEffect } from 'react';
import CartIcon from './CartIcon.js';
import classes from './HeaderCartButton.module.css';
import cartContext from '../../store/cart-context';

const HeaderCartButton = (props) => {
    const cartCtx = useContext(cartContext);
    const {items} = cartCtx;
    const countCart = items.reduce((currentCount, ind) => {
        return currentCount + ind.amount;
    }, 0);
    const btnClass = `${classes.button} ${classes.bump}`;
    // useEffect()
    return <button className={btnClass} onClick={props.onShowModal}>
        <span className={classes.icon}>
            <CartIcon />
        </span>
        <span>Your Cart</span>
        <span className={classes.badge}>{countCart}</span>
    </button>
}

export default HeaderCartButton;
